catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var releaseName = g_form.getValue('release_name');
	var releaseVersion = g_form.getValue('release_version');
    var subject = g_form.getValue('short_description');
	var isReleaseName = subject.indexOf(releaseName) > -1;
	var isReleaseVersion = subject.indexOf(releaseVersion) > -1;
	var isSubject = (subject == releaseName + ' ' + releaseVersion);


    if (!subject) {
        g_form.setValue('short_description', releaseName);
    }

	if (subject && isReleaseVersion) {
		g_form.setValue('short_description', releaseName + ' ' + releaseVersion);
	}
}
