sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var colorType = ["green", "orange"];
    var noteType = [];
    var description = [];

	// Get Release notes list
	var grRN = new GlideRecord('x_803596_release_0_release_notes');
	grRN.addQuery('release', current.sys_id);
	grRN.query();

	while(grRN.next()) {
		noteType.push(grRN.note_type.getDisplayValue());
		description.push(grRN.description);
	}

    // Release notes
    template.print('<table role="presentation"><tbody><tr>');

    for (var i = 0; i < noteType.length; i++) {
        template.print('<tr><td style="max-width: 150px;vertical-align: top;" align="center"><div style="padding: 3px 15px; border: 1px solid; border-radius: 30px;background-color:' + colorType[i] + ';color:#fff;font-size:13px;">' + noteType[i] + '</div></td><td style="padding-left: 15px;white-space:pre;padding-bottom: 10px;">' + description[i] + '</td></tr>');
    }

    template.print('</tr></tbody></table>');

    // Additional Information
    template.print('<table role="presentation"><tbody><tr>');

    template.print('<tr "><td style="padding-top: 20px;">' + current.description + '</td></tr>');

    template.print('</tr></tbody></table>');

})(current, template, email, email_action, event);
