sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template, /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Variables
    var colorProps = gs.getProperty("x_803596_release_0.release_note_type_color");
    var colorType = JSON.parse(colorProps);
    var replyTo = gs.getProperty("x_803596_release_0.release_email_reply_to");
    var notes = [];

    // Mail setting
    if (replyTo) email.setReplyTo(replyTo);

    // Get Release notes list
    var grRN = new GlideRecord('x_803596_release_0_release_notes');
    grRN.addQuery('release', current.sys_id);
    grRN.query();

    while (grRN.next()) {
		try {
        var value = grRN.note_type.toString();
        var name = grRN.note_type.getDisplayValue();
        var desc = grRN.description.getDisplayValue();

        colorType.forEach(function(item) {
            if (item.name == value) {
                notes.push({
                    name: name,
                    color: item.color,
                    desc: desc
                });
            }
        });
		} catch (e) {
			gs.debug("mail_script:release_email_notification error: " + e);
		}

        // var i = 0;
        // while (i < colorType.length) {
        //     var item = colorType[i];

        //     if (item.name == value) {
        //         notes.push({
        //             name: name,
        //             color: item.color,
        //             desc: desc
        //         });
        //     }
        //     i++;
        // }
    }

    // Description
    template.print('<table role="presentation"><tbody><tr>');
    template.print('<tr><td style="padding-bottom: 20px;">' + current.description + '</td></tr>');
    template.print('</tr></tbody></table>');

    // Release notes
    template.print('<table role="presentation"><tbody><tr>');

    for (var i = 0; i < notes.length; i++) {
        template.print('<tr><td style="max-width: 150px;vertical-align: top;" align="center"><div style="padding: 3px 15px; border: 1px solid; border-radius: 30px;background-color:' + notes[i].color + ';color:#fff;font-size:13px;">' + notes[i].name + '</div></td><td style="padding-left: 15px;white-space:pre;padding-bottom: 10px;">' + notes[i].desc + '</td></tr>');
    }

    template.print('</tr></tbody></table>');

    // Additional Information
    template.print('<table role="presentation"><tbody><tr>');
    template.print('<tr><td style="padding-top: 20px;">' + current.additional_information + '</td></tr>');
    template.print('</tr></tbody></table>');

})(current, template, email, email_action, event);
